mapper.update:
update claim


		sales_idx = #{sales_idx},


		type = #{type},


		claim_reson = #{claim_reson},


		status = #{status}


where claim_idx = #{claim_idx}

mapper.select:
select * from claim

	del_yn = false

		and sales_idx = #{sales_idx}


		and type = #{type}


		and claim_reson like concat('%',#{claim_reson},'%')


		and status = #{status}


		and claim_date > #{start_date}


		and claim_date < #{end_date}


		and claim_date = #{claim_date}



	order by ${orderColumn}

		${orderDirection}


limit #{cnt} offset #{offset}

select ceil(count(*)*1.0/#{cnt}) from claim

	del_yn = false

		and sales_idx = #{sales_idx}


		and type = #{type}


		and claim_reson like concat('%',#{claim_reson},'%')


		and status = #{status}


		and claim_date > #{start_date}


		and claim_date < #{end_date}


		and claim_date = #{claim_date}
