mapper.update:
UPDATE custom
SET del_yn = 1
WHERE custom_idx = #{custom_idx}

mapper.select:
SELECT * FROM custom WHERE del_yn = 0
AND ( custom_name LIKE CONCAT('%', #{search}, '%') OR custom_owner LIKE CONCAT('%', #{search}, '%') OR custom_phone LIKE CONCAT('%', #{search}, '%') )
ORDER BY ${sortKey} ${sortOrder}
ORDER BY custom_idx DESC
LIMIT #{start}, #{size}
