mapper.select:
select * from `user`


		user_name like concat('%',#{user_name},'%')


		hire_date > #{start_date}


		hire_date < #{end_date}


		hire_date = #{hire_date}


		status = #{status}


		annual_cnt > #{annual}


		job_idx = #{job_idx}


		dept_idx = #{dept_idx}



	order by ${orderColumn}

		${orderDirection}



	limit #{cnt} offset #{offset}

select ceil(count(*)*1.0/#{cnt}) from `user`


		user_name like concat('%',#{user_name},'%')


		hire_date > #{start_date}


		hire_date < #{end_date}


		hire_date = #{hire_date}


		status = #{status}


		annual_cnt > #{annual}


		job_idx = #{job_idx}


		dept_idx = #{dept_idx}
