mapper.insert:
INSERT INTO product (
    product_name, purchase_price, selling_price, discount_rate, product_standard, category_idx
) 
VALUES (
    #{product_name}, #{purchase_price}, #{selling_price}, #{discount_rate}, #{product_standard}, #{category_idx}
)

mapper.update:
UPDATE product
SET del_yn = 1
WHERE product_idx = #{product_idx}

mapper.select:
SELECT p.*, c.category_name FROM product p LEFT JOIN category c ON p.category_idx = c.category_idx WHERE p.del_yn = '0'
AND (p.product_name LIKE CONCAT('%', #{search}, '%') OR p.product_idx LIKE CONCAT('%', #{search}, '%'))
AND p.category_idx = #{category}
ORDER BY
p.selling_price ASC p.selling_price DESC p.product_idx DESC
LIMIT #{start}, #{size}

SELECT COUNT(*) FROM product WHERE del_yn = '0'
AND (product_name LIKE CONCAT('%', #{search}, '%') OR product_idx LIKE CONCAT('%', #{search}, '%'))
AND category_idx = #{category}

select * from product p
left join product_option po on p.product_idx = po.product_idx 
left join combined c on po.combined_idx = c.combined_idx

	p.del_yn= false

		and p.product_idx = #{product_idx}


		and p.category_idx = #{category_idx}


		AND (p.product_name LIKE CONCAT('%', #{search}, '%')
		OR p.product_idx LIKE CONCAT('%', #{search}, '%'))



	order by ${orderColumn}

		${orderDirection}


limit #{cnt} offset #{offset}

select ceil(count(*)*1.0/#{cnt}) from product p left join product_option po on p.product_idx = po.product_idx left join combined c on po.combined_idx = c.combined_idx
p.del_yn= false
and p.product_idx = #{product_idx}
and p.category_idx = #{category_idx}
AND (p.product_name LIKE CONCAT('%', #{search}, '%') OR p.product_idx LIKE CONCAT('%', #{search}, '%'))
