mapper.insert:
INSERT INTO product (
    product_name, purchase_price, selling_price, discount_rate, product_standard, category_idx
) 
VALUES (
    #{product_name}, #{purchase_price}, #{selling_price}, #{discount_rate}, #{product_standard}, #{category_idx}
)

mapper.update:
UPDATE product
SET del_yn = 1
WHERE product_idx = #{product_idx}

mapper.select:
SELECT * FROM product WHERE del_yn = '0'
AND (product_name LIKE CONCAT('%', #{search}, '%') OR product_idx LIKE CONCAT('%', #{search}, '%'))
AND category_idx = #{category}
ORDER BY
selling_price ASC selling_price DESC product_idx DESC
LIMIT #{start}, #{size}

SELECT COUNT(*) FROM product WHERE del_yn = '0'
AND (product_name LIKE CONCAT('%', #{search}, '%') OR product_idx LIKE CONCAT('%', #{search}, '%'))
AND category_idx = #{category}
