mapper.insert:
insert into sales (customer, customer_phone, customer_address, payment_option

	,payment_date


	,status

)
values (#{customer},#{customer_phone},#{customer_address},#{payment_option}

	,#{payment_date}


	,#{status}

)

mapper.select:
select * from sales

	del_yn = false

		and customer = #{customer}


		and customer_phone = #{customer_phone}


		and customer_address = #{customer_address}


		and payment_option = #{payment_option}


		and payment_date >= #{start_date}


		and payment_date <= #{end_date}


		and payment_date = #{payment_date}


		and status = #{status}



	order by ${orderColumn}

		${orderDirection}


limit #{cnt} offset #{offset}

select ceil(count(*)*1.0/#{cnt}) from sales

	del_yn = false

		and customer = #{customer}


		and customer_phone = #{customer_phone}


		and customer_address = #{customer_address}


		and payment_option = #{payment_option}


		and payment_date >= #{start_date}


		and payment_date <= #{end_date}


		and payment_date = #{payment_date}


		and status = #{status}
