mapper.insert:
insert into stock (product_idx,stock_cnt, zone_idx, type, user_idx, warehouse_idx

	,product_option_idx


	,manufacture


	,expiration

)
values (#{product_idx},#{stock_cnt},#{zone_idx},#{type},#{user_idx}, #{warehouse_idx}

	,#{product_option_idx}


	,#{manufacture}


	,#{expiration}

)

mapper.select:
select 
	s.*
	,w.warehouse_name
	,z.zone_name
	,u.user_name
	,p.product_name
	,c.combined_name
from stock s
	join warehouse w on w.warehouse_idx = s.warehouse_idx
	join `zone` z on z.zone_idx = s.zone_idx
	join `user` u on u.user_idx = s.user_idx
	join product p on p.product_idx = s.product_idx
	left join product_option po on po.product_option_idx = s.product_option_idx
	left join combined c on c.combined_idx = po.combined_idx

	s.del_yn = false

		and s.warehouse_idx = #{warehouse_idx}


		and s.zone_idx = #{zone_idx}


		and s.user_idx = #{user_idx}


		and s.product_idx = #{product_idx}


		and s.type = #{type}


		and s.product_option_idx = #{product_option_idx}


		and s.manufacture = #{manufacture}


		and s.expiration = #{expiration}



	order by ${orderColumn}

		${orderDirection}



	limit #{cnt} offset #{offset}

select ceil(count(*)*1.0/#{cnt}) from (
	select 
	s.*
	,w.warehouse_name
	,z.zone_name
	,u.user_name
	,p.product_name
	,c.combined_name
from stock s
	join warehouse w on w.warehouse_idx = s.warehouse_idx
	join `zone` z on z.zone_idx = s.zone_idx
	join `user` u on u.user_idx = s.user_idx
	join product p on p.product_idx = s.product_idx
	left join product_option po on po.product_option_idx = s.product_option_idx
	left join combined c on c.combined_idx = po.combined_idx

	s.del_yn = false

		and s.warehouse_idx = #{warehouse_idx}


		and s.zone_idx = #{zone_idx}


		and s.user_idx = #{user_idx}


		and s.product_idx = #{product_idx}


		and s.type = #{type}


		and s.product_option_idx = #{product_option_idx}


		and s.manufacture = #{manufacture}


		and s.expiration = #{expiration}


) as total

select s.product_idx ,p.product_name
,s.product_option_idx ,c.combined_name
,s.manufacture
,s.expiration
,s.warehouse_idx ,w.warehouse_name
,s.zone_idx ,z.zone_name
,sum(s.stock_cnt) as stock_sum from stock s join product p on s.product_idx = p.product_idx left join product_option po on s.product_option_idx = po.product_option_idx left join combined c on po.combined_idx = c.combined_idx join warehouse w on w.warehouse_idx = s.warehouse_idx join `zone` z on s.zone_idx = z.zone_idx
s.del_yn = false
and s.product_idx = #{product_idx}
and s.product_option_idx = #{product_option_idx}
and s.warehouse_idx = #{warehouse_idx}
and s.zone_idx = #{zone_idx}
and s.manufacture = #{manufacture}
and s.expiration = #{expiration}
group by s.product_idx
,s.product_option_idx
,s.manufacture
,s.expiration
,s.warehouse_idx
,s.zone_idx
limit #{cnt} offset #{offset}

select ceil(count(*)*1.0/#{cnt}) from ( select s.product_idx ,p.product_name
,s.product_option_idx ,c.combined_name
,s.manufacture
,s.expiration
,s.warehouse_idx ,w.warehouse_name
,s.zone_idx ,z.zone_name
,sum(s.stock_cnt) as stock_sum from stock s join product p on s.product_idx = p.product_idx left join product_option po on s.product_option_idx = po.product_option_idx left join combined c on po.combined_idx = c.combined_idx join warehouse w on w.warehouse_idx = s.warehouse_idx join `zone` z on s.zone_idx = z.zone_idx
s.del_yn = false
and s.product_idx = #{product_idx}
and s.product_option_idx = #{product_option_idx}
and s.warehouse_idx = #{warehouse_idx}
and s.zone_idx = #{zone_idx}
and s.manufacture = #{manufacture}
and s.expiration = #{expiration}
group by s.product_idx
,s.product_option_idx
,s.manufacture
,s.expiration
,s.warehouse_idx
,s.zone_idx
) as stock_group
